export function getSubcategoriesByCategory(categoryId: number): TagSubcategory[] {
  return TAG_SUBCATEGORIES.filter(sub => sub.categoryId === categoryId);
}

export function getVisibleSubcategories(categoryId: number): TagSubcategory[] {
  return TAG_SUBCATEGORIES.filter(sub => 
    sub.categoryId === categoryId && sub.isVisible
  );
}

export function getSuggestedSubcategories(categoryId: number): TagSubcategory[] {
  return TAG_SUBCATEGORIES.filter(sub => 
    sub.categoryId === categoryId && sub.isSuggested
  );
}

export function getSubcategoryById(id: number): TagSubcategory | undefined {
  return TAG_SUBCATEGORIES.find(sub => sub.id === id);
}